mapper.select:
SELECT * FROM agent_info  a
where phoneNumber=#{phoneNumber} 
and pwd=#{pwd}
and a.isValid=0

select  token from  agent_info  a
where phoneNumber=#{phoneNumber} 
and a.isvalid=0

select * from agent_info a 
where phoneNumber=#{phoneNumber}
and a.isValid=0

mapper.update:
update   agent_info a
set token=#{token},tokenTime=now(),pwd=#{pwd}
where phoneNumber=#{phoneNumber} 
and a.isvalid=0

mapper.select:
select * from
garten_info g
where isValid=0
and agent=#{agentId}

	and g.gartenName=#{name}


	and g.phoneNumber=#{phoneNumber}

mapper.update:
update garten_info 
set
gartenName=#{gartenName}, name=#{name}, phoneNumber=#{phoneNumber},contractNumber=#{contractNumber},
contractStart=#{contractStart} ,contractEnd=#{contractEnd}, organizationCode=#{organizationCode},
province=#{province} ,city=#{city},countries=#{countries}, address=#{address},charge=#{charge}
where gartenId=#{gartenId}

update agent_audit set
isvalid=1
where auditId=#{auditId}

mapper.select:
SELECT * FROM wuliao_order  w
where w.isvalid=0


		and agentId=#{agentId}
	
	
		and w.state=#{state}
	
	
		and w.wuliaoId=#{wuliaoId}
