 o.isValid=0 
 
 	and o.state=#{state}
 
 
 	and o.type=#{type}
 
 
 	and o.gartenId=#{gartenId}
 
 
 	and g.province=#{province}
 
 
 	and g.city=#{city}
 
 
 	and g.countries=#{countries}
 
 order by o.orderTime desc

