select *  from `worker＿info` w LEFT JOIN info_log i on i.id=w.workerId 
 where 
 token=#{token} 
 and w.job=i.job
 and i.type=#{type}
 and w.isValid=0 and i.isValid=0

SELECT *		FROM
baby_info b LEFT JOIN baby_check_log c on b.babyId=c.babyId
WHERE
b.classId = #{classId}
and UNIX_TIMESTAMP(c.time)=#{time}
and b.isValid=0

SELECT *  FROM
garten_lesson 
where
 UNIX_TIMESTAMP(time)=#{time}
and classId=#{classId}
and isValid=0

mapper.update:
UPDATE baby_info b
SET b.allergy =#{allergy}, b.babyName =#{babyName}, b.birthday =FROM_UNIXTIME(#{birthday},  '%Y-%m-%d %H:%i:%s'), b.health =#{health}, 
b.height =#{height}, b.hobby =#{hobby}, b.specialty =#{specialty}, b.classId =#{classId},b.weight=#{weight},b.sex=#{sex}
where 
 b.babyId=#{babyId}
and b.isValid=0

UPDATE baby_leave_log b
set leaveState=1
where leaveId=#{leaveId}

UPDATE info_log i
set state=1
where infoId=#{infoId}

UPDATE worker＿info b
set workerName=#{workerName},sex=#{sex},age=#{age}
where token=#{token}

UPDATE worker＿info b
set headImg=#{imgAddress}
where token=#{token}

UPDATE baby_check_log b set checkId=checkId
, b.stateLate=1
, b.stateEarly=1 where checkId=#{checkId}

UPDATE worker_check_log c set checkId=checkId
, c.stateLate=1
, c.stateEarly=1 where checkId=#{checkId}

UPDATE baby_performance_log p
